		if(mode == "design"){
			return [this._controlActionSet(), this._formatActionSet(),
			        this._tableActionSet(), this._widgetActionSet(), this._modeActionSet()];
		}else if(mode == "sourcecode"){
			return [this._modeActionSet()];
		}
